mapper.update:
update ssmbuild.admin
set name = #{name},password = #{password}
where name = #{name}

update ssmbuild.user
set username = #{username},password = #{password},gender=#{gender},age=#{age},studentNum=#{studentNum},phoneNum=#{phoneNum},money=#{money},college=#{college},grade=#{grade}
where id = #{id}

update ssmbuild.books
set bookName = #{bookName},bookCounts=#{bookCounts},detail=#{detail},date=#{date},author=#{author},type=#{type},money=#{money},count=#{count}
where bookID = #{bookID}
